import React, { useState } from 'react';
import config from '../config';
import './../styles/components.css';

const OptimizationControls = ({ onOptimize, loading, originalFile }) => {
  const [width, setWidth] = useState(config.DEFAULT_WIDTH);
  const [format, setFormat] = useState('webp');
  const [quality, setQuality] = useState(config.DEFAULT_QUALITY);
  
  const handleOptimize = () => {
    onOptimize({
      width: parseInt(width),
      format,
      quality: parseInt(quality)
    });
  };
  
  const sizePresets = [
    { label: 'Small (480px)', value: 480 },
    { label: 'Medium (800px)', value: 800 },
    { label: 'Large (1200px)', value: 1200 },
    { label: 'Full HD (1920px)', value: 1920 }
  ];
  
  const formatOptions = [
    { label: 'WebP (Best)', value: 'webp' },
    { label: 'JPEG', value: 'jpeg' },
    { label: 'PNG', value: 'png' }
  ];
  
  return (
    <div className="controls-container">
      <div className="controls-header">
        <h3>Optimization Settings</h3>
        {originalFile && (
          <div className="file-info">
            Selected: <strong>{originalFile.name}</strong>
          </div>
        )}
      </div>
      
      <div className="controls-grid">
        <div className="control-group">
          <label>Width (px)</label>
          <div className="size-presets">
            {sizePresets.map(preset => (
              <button
                key={preset.value}
                type="button"
                className={`preset-btn ${width === preset.value ? 'active' : ''}`}
                onClick={() => setWidth(preset.value)}
              >
                {preset.label}
              </button>
            ))}
          </div>
          <input
            type="number"
            value={width}
            onChange={(e) => setWidth(e.target.value)}
            min="100"
            max="4000"
          />
        </div>
        
        <div className="control-group">
          <label>Format</label>
          <div className="format-buttons">
            {formatOptions.map(option => (
              <button
                key={option.value}
                type="button"
                className={`format-btn ${format === option.value ? 'active' : ''}`}
                onClick={() => setFormat(option.value)}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>
        
        <div className="control-group">
          <label>Quality: {quality}%</label>
          <input
            type="range"
            min="1"
            max="100"
            value={quality}
            onChange={(e) => setQuality(e.target.value)}
          />
          <div className="quality-labels">
            <span>Smaller</span>
            <span>Balanced</span>
            <span>Better</span>
          </div>
        </div>
      </div>
      
      <button
        className="optimize-btn"
        onClick={handleOptimize}
        disabled={loading || !originalFile}
      >
        {loading ? '⏳ Optimizing...' : '✨ Optimize Image'}
      </button>
    </div>
  );
};

export default OptimizationControls;
