import React from 'react';
import './../styles/components.css';

const ResultsDisplay = ({ stats }) => {
  if (!stats) return null;
  
  const savings = ((1 - stats.size / stats.originalSize) * 100).toFixed(1);
  const sizeReduction = (stats.originalSize - stats.size) / 1024;
  
  const downloadImage = () => {
    const link = document.createElement('a');
    link.href = stats.optimizedImageUrl;
    link.download = `optimized-image.${stats.format}`;
    link.click();
  };
  
  return (
    <div className="results-container">
      <div className="stats-grid">
        <div className="stat-item">
          <div className="stat-label">Format</div>
          <div className="stat-value">{stats.format.toUpperCase()}</div>
        </div>
        
        <div className="stat-item">
          <div className="stat-label">Size</div>
          <div className="stat-value">{(stats.size / 1024).toFixed(2)} KB</div>
        </div>
        
        <div className="stat-item">
          <div className="stat-label">Dimensions</div>
          <div className="stat-value">{stats.width} Ã— {stats.height}</div>
        </div>
      </div>
      
      {savings > 0 && (
        <div className="savings-badge">
          <span className="savings-icon">ðŸ’¾</span>
          <span className="savings-text">
            Saved {savings}% ({sizeReduction.toFixed(2)} KB)
          </span>
        </div>
      )}
      
      <button className="download-btn" onClick={downloadImage}>
        ðŸ’¾ Download Optimized Image
      </button>
    </div>
  );
};

export default ResultsDisplay;
