import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import config from '../config';
import './../styles/components.css';

const ImageUploader = ({ onUpload }) => {
  const [error, setError] = useState('');
  
  const onDrop = useCallback((acceptedFiles, rejectedFiles) => {
    setError('');
    
    if (rejectedFiles.length > 0) {
      setError('Please upload a valid image file (JPG, PNG, WebP, GIF)');
      return;
    }
    
    const file = acceptedFiles[0];
    
    if (file.size > config.MAX_FILE_SIZE) {
      setError(`File too large. Maximum size: ${config.MAX_FILE_SIZE / 1024 / 1024}MB`);
      return;
    }
    
    const previewUrl = URL.createObjectURL(file);
    onUpload(file, previewUrl);
  }, [onUpload]);
  
  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif', '.webp']
    },
    maxFiles: 1,
    maxSize: config.MAX_FILE_SIZE
  });
  
  return (
    <div className="uploader-container">
      <div 
        {...getRootProps()} 
        className={`dropzone ${isDragActive ? 'active' : ''}`}
      >
        <input {...getInputProps()} />
        <div className="upload-icon">üìÅ</div>
        <h3>Upload Image</h3>
        {isDragActive ? (
          <p>Drop the image here...</p>
        ) : (
          <p>Drag & drop or click to select</p>
        )}
        <p className="formats">Supported: JPG, PNG, WebP, GIF</p>
        <p className="max-size">Max size: 10MB</p>
      </div>
      
      {error && <div className="upload-error">{error}</div>}
    </div>
  );
};

export default ImageUploader;
