import React, { useState } from 'react';
import ImageUploader from './components/ImageUploader';
import OptimizationControls from './components/OptimizationControls';
import ResultsDisplay from './components/ResultsDisplay';
import { optimizeImage } from './services/api';
import './styles/App.css';

function App() {
  const [originalImage, setOriginalImage] = useState(null);
  const [optimizedImage, setOptimizedImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [stats, setStats] = useState(null);
  
  const handleImageUpload = (file, previewUrl) => {
    setOriginalImage({ file, previewUrl });
    setOptimizedImage(null);
    setError('');
    setStats(null);
  };
  
  const handleOptimize = async (options) => {
    if (!originalImage) return;
    
    setLoading(true);
    setError('');
    
    try {
      const result = await optimizeImage(originalImage.file, options);
      
      if (result.success) {
        setOptimizedImage(result.optimizedImage);
        setStats({
          optimizedImageUrl: result.optimizedImage,
          format: result.format,
          size: result.size,
          width: result.width,
          height: result.height,
          originalSize: originalImage.file.size
        });
      } else {
        setError(result.error || 'Optimization failed');
      }
      
    } catch (err) {
      setError(err.message || 'Something went wrong');
      console.error('Optimization error:', err);
    } finally {
      setLoading(false);
    }
  };
  
  const formatBytes = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };
  
  const savings = stats && originalImage ? 
    ((1 - stats.size / originalImage.file.size) * 100).toFixed(1) : 0;
  
  return (
    <div className="app">
      <header className="app-header">
        <h1>üñºÔ∏è AWS Image Optimizer</h1>
        <p className="subtitle">Upload images and optimize them using AWS Lambda</p>
      </header>
      
      <main className="app-main">
        <div className="upload-section">
          <ImageUploader onUpload={handleImageUpload} />
        </div>
        
        {originalImage && (
          <>
            <div className="controls-section">
              <OptimizationControls 
                onOptimize={handleOptimize}
                loading={loading}
                originalFile={originalImage.file}
              />
            </div>
            
            <div className="comparison-section">
              <div className="image-comparison">
                {/* Original Image */}
                <div className="image-box original-box">
                  <div className="box-header">
                    <h3>üì∑ Original Image</h3>
                    <div className="file-name">{originalImage.file.name}</div>
                  </div>
                  <div className="image-container">
                    <img src={originalImage.previewUrl} alt="Original" />
                  </div>
                  <div className="stats-box">
                    <div className="stat-row">
                      <span className="stat-label">Size:</span>
                      <span className="stat-value original-size">
                        {formatBytes(originalImage.file.size)}
                      </span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">Type:</span>
                      <span className="stat-value">{originalImage.file.type}</span>
                    </div>
                  </div>
                </div>
                
                {/* Optimized Image */}
                <div className="image-box optimized-box">
                  <div className="box-header">
                    <h3>‚ö° Optimized Image</h3>
                    {optimizedImage && (
                      <div className="format-badge">{stats?.format?.toUpperCase()}</div>
                    )}
                  </div>
                  <div className="image-container">
                    {optimizedImage ? (
                      <img src={optimizedImage} alt="Optimized" />
                    ) : (
                      <div className="placeholder">
                        <div className="placeholder-icon">üîÑ</div>
                        <p>Optimized image will appear here</p>
                      </div>
                    )}
                  </div>
                  
                  {optimizedImage && stats && (
                    <div className="stats-box">
                      <div className="stat-row">
                        <span className="stat-label">Size:</span>
                        <span className="stat-value optimized-size">
                          {formatBytes(stats.size)}
                        </span>
                      </div>
                      <div className="stat-row">
                        <span className="stat-label">Dimensions:</span>
                        <span className="stat-value">
                          {stats.width} √ó {stats.height}px
                        </span>
                      </div>
                      
                      {savings > 0 && (
                        <div className="savings-row">
                          <span className="savings-icon">üíæ</span>
                          <span className="savings-text">
                            Saved {savings}% space
                          </span>
                        </div>
                      )}
                      
                      <ResultsDisplay stats={stats} />
                    </div>
                  )}
                </div>
              </div>
            </div>
          </>
        )}
        
        {error && (
          <div className="error-message">
            ‚ö†Ô∏è {error}
          </div>
        )}
        
        {loading && (
          <div className="loading-overlay">
            <div className="spinner"></div>
            <p>Processing image...</p>
          </div>
        )}
      </main>
      
      <footer className="app-footer">
        <p>Powered by AWS Lambda + API Gateway + S3</p>
        <p className="api-info">
          API Endpoint: https://ke3a91dqwe.execute-api.ap-south-1.amazonaws.com/prod
        </p>
      </footer>
    </div>
  );
}

export default App;
