import React from 'react';
import './../styles/components.css';

const ResultsDisplay = ({ stats }) => {
  if (!stats) return null;
  
  const downloadImage = () => {
    if (!stats.optimizedImageUrl) return;
    
    // Extract base64 from data URL
    const base64Data = stats.optimizedImageUrl;
    const base64String = base64Data.split(',')[1];
    const mimeType = base64Data.split(',')[0].split(':')[1].split(';')[0];
    
    // Convert base64 to blob
    const byteCharacters = atob(base64String);
    const byteNumbers = new Array(byteCharacters.length);
    
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], { type: mimeType });
    
    // Create download link
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = `optimized-image.${stats.format}`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(link.href);
  };
  
  return (
    <div className="results-container">
      <button className="download-btn" onClick={downloadImage}>
        ðŸ’¾ Download Optimized Image
      </button>
      
      <div className="download-info">
        File will be saved as: <code>optimized-image.{stats.format}</code>
      </div>
    </div>
  );
};

export default ResultsDisplay;
