const config = {
    // ⭐ YOUR API GATEWAY ⭐
    API_ENDPOINT: "https://ke3a91dqwe.execute-api.ap-south-1.amazonaws.com/prod/optimize",
    
    // Optimization presets
    OPTIMIZATION_LEVELS: [
        { value: 'basic', label: 'Basic (Fast)', description: 'Quick processing for thumbnails' },
        { value: 'balanced', label: 'Balanced', description: 'Optimal quality and size' },
        { value: 'advanced', label: 'Advanced (AI)', description: 'Best quality with AI enhancements' }
    ],
    
    FORMATS: [
        { value: 'webp', label: 'WebP', description: 'Best modern format (recommended)' },
        { value: 'jpeg', label: 'JPEG', description: 'Universal compatibility' },
        { value: 'png', label: 'PNG', description: 'Lossless quality' }
    ],
    
    // Defaults
    DEFAULT_WIDTH: 1200,
    DEFAULT_QUALITY: 85,
    DEFAULT_OPTIMIZATION: 'balanced',
    
    // Limits
    MAX_FILE_SIZE: 10 * 1024 * 1024, // 10MB
    SUPPORTED_TYPES: ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/gif', 'image/bmp']
};

export default config;
